import React from "react";

import Card from "../components/card";
import FormGroup from "../components/formGroup";
import SelectMenu from "../components/selectMenu";
import selectMenu from "../components/selectMenu";

class ConsultaLancamentos extends React.Component {

    state = {
        dataInicial: null,
        dataFinal: null
    }

    render() {
        const lista = [
            {label: 'Despesa', value:'DESPESA'},
            {label: 'Receita', value:'RECEITA'}
        ];

        return (
            <Card title="Consulta lançamentos">
                <div class="row">
                    <div class="col-lg-12">
                        <div class="bs-component">
                            <div className="d-flex justify-content-around mt-3" >
                                <FormGroup htmlFor="inputDataInicial" label="Data inicial: *">
                                    <input type="date"
                                        value={this.state.descricao}
                                        onChange={e => this.setState({ descricao: e.target.value })}
                                        className="form-control"
                                        id="inputDataInicial" />
                                </FormGroup>
                                <FormGroup htmlFor="inputDataFinal" label="Data final: *">
                                    <input type="date"
                                        value={this.state.descricao}
                                        onChange={e => this.setState({ descricao: e.target.value })}
                                        className="form-control"
                                        id="inputDataFinal" />
                                </FormGroup>
                                <FormGroup htmlFor="inputLancamento" label="Tipo lançamento: *">
                                    <SelectMenu lista={lista} />
                                </FormGroup>
                            </div>
                            <div className="d-flex justify-content-center mt-3" >
                                <button onClick={this.cadastrar} type="button" className="btn btn-success" style={{ marginRight: "30px" }} >Buscar</button>
                                <button type="button" className="btn btn-danger">Cadastrar</button>
                            </div>
                        </div>
                    </div>
                </div>
            </Card>
        );
    }
}

export default ConsultaLancamentos;
