import React from "react";
import { Link } from "react-router-dom";

import Card from "../../components/card";
import Checkbox from "../../components/checkbox";
import FormGroup from "../../components/formGroup";
import SelectMenu from "../../components/selectMenu";

class CadastroLancamento extends React.Component {

    state = {
        nome: "",
        descricao: "",
        dataVencimento: null,
        dataFechamento: null,
        limite: "0,00"
    }

    cadastrar = () => {
        console.log(this.state);
    }

    render() {
        const listaTipos = [
            { label: 'Despesa', value: 'DESPESA' },
            { label: 'Receita', value: 'RECEITA', selected: 'selected' }
        ];

        const listaRecorrencia = [
            { label: 'Lançamento recorrente', value: 'recorrente' }
        ];

        const listaConta = [
            { label: 'Banco 1', value: 'Banco1', selected: 'selected' },
            { label: 'Banco 2', value: 'Banco2' }
        ]

        const listaCartao = [
            { label: '-', value: 'nenhum', selected: 'selected' },
            { label: 'Cartão 1', value: 'cartao1' },
            { label: 'Cartão 2', value: 'cartao2' }
        ]

        const listaFrequencia = [
            { label: 'Diário', value: 'diario' },
            { label: 'Semanal', value: 'semestral' },
            { label: 'Quinzenal', value: 'quinzenal' },
            { label: 'Mensal', value: 'mensal', selected: 'selected' },
            { label: 'Bimestral', value: 'bimestarl' },
            { label: 'Trimestral', value: 'trimestral' },
            { label: 'Semestral', value: 'semestral' },
            { label: 'Anual', value: 'anual' }
        ];

        return (
            <Card title="Cadastro de lançamento">
                <div className="row">
                    <div className="col-lg-12">
                        <div className="bs-component">
                            {/* <FormGroup htmlFor="inputNome" label="Nome: *">
                                <input type="text"
                                    value={this.state.nome}
                                    onChange={e => this.setState({ nome: e.target.value })}
                                    className="form-control"
                                    id="inputNome"
                                    name="nome"
                                    placeholder="Digite o Nome" />
                            </FormGroup> */}
                            <FormGroup htmlFor="inputDescricao" label="Descrição: *">
                                <input type="text"
                                    value={this.state.descricao}
                                    onChange={e => this.setState({ descricao: e.target.value })}
                                    className="form-control"
                                    id="inputDescricao"
                                    placeholder="Digite a descrição" />
                            </FormGroup>
                            <div className="d-flex justify-content-around mt-3" >
                                <FormGroup htmlFor="inputTipoLancamento" label="Tipo lançamento: *">
                                    <SelectMenu lista={listaTipos} clazz="form-control" />
                                </FormGroup>
                                {/* <FormGroup htmlFor="inputConta" label="Conta: *">
                                    <SelectMenu lista={listaConta} clazz="form-control" />
                                </FormGroup>
                                <FormGroup htmlFor="inputCartao" label="Cartão:">
                                    <SelectMenu lista={listaCartao} clazz="form-control" />
                                </FormGroup>
                                <FormGroup htmlFor="inputDataVencimento" label="Data de vencimento: *">
                                    <input type="date"
                                        value={this.state.descricao}
                                        onChange={e => this.setState({ descricao: e.target.value })}
                                        className="form-control"
                                        id="inputDataVencimento" />
                                </FormGroup> */}
                                <FormGroup htmlFor="inputValor" label="Valor:">
                                    <input type="text"
                                        pattern="[0-9]*"
                                        value={this.state.limite}
                                        onChange={e => this.setState({ limite: e.target.value })}
                                        className="form-control"
                                        id="inputValor"
                                        placeholder="Digite o limite do cartão" />
                                </FormGroup>
                            </div>
                            {/* <div className="d-flex justify-content-around mt-3" >
                                <Checkbox lista={listaRecorrencia} />
                                <FormGroup htmlFor="inputRecorrencia" label="Recorrência:">
                                    <SelectMenu lista={listaFrequencia} clazz="form-control" />
                                </FormGroup>
                                <FormGroup htmlFor="inputDataFinal" label="Data de término:">
                                    <input type="date"
                                        value={this.state.descricao}
                                        onChange={e => this.setState({ descricao: e.target.value })}
                                        className="form-control"
                                        id="inputDataFinal" />
                                </FormGroup>
                            </div> */}
                            <div className="d-flex justify-content-center mt-3" >
                                <Link to="/consulta-lancamentos"><button onClick={this.cadastrar} type="button" className="btn btn-success" style={{ marginRight: "30px" }} >Salvar</button></Link>
                                <Link to="/consulta-lancamentos"><button type="button" className="btn btn-danger">Cancelar</button></Link>
                            </div>
                        </div>
                    </div>
                </div>
            </Card>
        );
    }
}

export default CadastroLancamento;
