import React from "react";

import Card from "../../components/card";
import FormGroup from "../../components/formGroup";
import SelectMenu from "../../components/selectMenu";
import LancamentosTable from "./lancamentosTable";

class ConsultaLancamentos extends React.Component {

    state = {
        dataInicial: "2022-07-01",
        dataFinal: "2022-07-31"
    }

    render() {
        const listaContas = [
            { label: 'Todas', value: 'todas', selected: 'selected' },
            { label: 'Banco 1', value: 'Banco1' },
            { label: 'Banco 2', value: 'Banco2' }
        ]

        const listaTipos = [
            { label: 'Despesa', value: 'DESPESA' },
            { label: 'Receita', value: 'RECEITA', selected: 'selected' }
        ];

        const lancamentos = [
            {id:1, descricao:'Salario', valor:'5000,00', tipo:'RECEITA', data:'07/07/2022', conta:"Banco 1", cartao:"-", situacao:'Pago'},
            {id:2, descricao:'Aluguel apartamento', valor:'1530,67', tipo:'RECEITA', data:'15/07/2022', conta:"Banco 2", cartao:"-", situacao:'Pago'}
        ]

        return (
            <Card title="Consulta lançamentos">
                <div class="row">
                    <div class="col-lg-12">
                        <div class="bs-component">
                            <div className="d-flex justify-content-around mt-3" >
                                <FormGroup htmlFor="inputDataInicial" label="Data inicial: *">
                                    <input type="date"
                                        value={this.state.dataInicial}
                                        onChange={e => this.setState({ dataInicial: e.target.value })}
                                        className="form-control"
                                        id="inputDataInicial" />
                                </FormGroup>
                                <FormGroup htmlFor="inputDataFinal" label="Data final: *">
                                    <input type="date"
                                        value={this.state.dataFinal}
                                        onChange={e => this.setState({ dataFinal: e.target.value })}
                                        className="form-control"
                                        id="inputDataFinal" />
                                </FormGroup>
                                <FormGroup htmlFor="inputConta" label="Conta: *">
                                    <SelectMenu lista={listaContas} clazz="form-control" />
                                </FormGroup>
                                <FormGroup htmlFor="inputTipoLancamento" label="Tipo lançamento: *">
                                    <SelectMenu lista={listaTipos} clazz="form-control" />
                                </FormGroup>
                            </div>
                            <div className="d-flex justify-content-center mt-3" >
                                <Link to="/cadastro-lancamento"><button onClick={this.cadastrar} type="button" className="btn btn-success" style={{ marginRight: "30px" }} >Buscar</button></Link>
                                <Link to="/consulta-lancamentos"><button type="button" className="btn btn-danger">Cadastrar</button></Link>
                            </div>
                        </div>
                    </div>
                </div>
                <div class="row mt-3">
                    <div class="col-md-12">
                        <div class="bs-component">
                            <LancamentosTable lancamentos={lancamentos} />
                        </div>
                    </div>
                </div>
            </Card>
        );
    }
}

export default ConsultaLancamentos;
