import React from "react";
import { Link } from "react-router-dom";

import Card from "../components/card";
import FormGroup from "../components/formGroup";
import SelectMenu from "../components/selectMenu";

import * as messages from "../components/toastr";
import CategoriaService from "../app/service/categoriaService";

class CadastroCategoria extends React.Component {

    constructor() {
        super()
        this.categoriaService = new CategoriaService();
    }

    componentDidMount() {
        this.buscarCategorias();
    }

    state = {
        nome: "",
        descricao: "",
        categoria: "",
        categoriaId: "",
        categorias: []
    }

    buscarCategorias() {
        this.categoriaService
            .obterCategorias()
            .then(response => {
                this.setState({
                    categorias: response.data.map(e => {return {'label':e.nome, 'value': e.nome}})
                })
            }).catch(error => {
                messages.mensagemErro('Erro ao importar categorias!')
                this.props.navigate('/consulta-lancamentos')
            })
    }

    handleChange = (event) => {
        const value = event.target.value
        const name = event.target.name

        this.setState({ [name]: value })
    }

    cadastrar = () => {
        const categoria = {
            id: this.state.lancamentoId,
            mes: this.state.mes,
            ano: this.state.ano,
            tipo: this.state.tipo,
            conta: this.state.conta,
            descricao: this.state.descricao,
            status: this.state.status,
            valor: this.state.valor,
            usuarioId: this.state.usuarioId
        }

        if (lancamento.id) {
            this.lancamentoService
                .atualizar(lancamento)
                .then(response => {
                    messages.mensagemSucesso('Lançamento atualizado com sucesso!')
                    this.props.navigate('/consulta-lancamentos')
                }).catch(error => {
                    messages.mensagemErro(error.response.data)
                })
        } else {
            this.lancamentoService
                .salvar(lancamento)
                .then(response => {
                    messages.mensagemSucesso('Lançamento salvo com sucesso!')
                    this.props.navigate('/consulta-lancamentos')
                }).catch(error => {
                    messages.mensagemErro(error.response.data)
                })
        }
    }

    render() {
        return (
            <Card title="Cadastro de Categoria">
                <div className="row">
                    <div className="col-lg-12">
                        <div className="bs-component">
                            <FormGroup htmlFor="inputNome" label="Nome: *">
                                <input type="text"
                                    value={this.state.nome}
                                    onChange={e => this.setState({ nome: e.target.value })}
                                    className="form-control"
                                    id="inputNome"
                                    name="nome"
                                    placeholder="Digite o Nome" />
                            </FormGroup>
                            <FormGroup htmlFor="inputDescricao" label="Descrição: *">
                                <input type="text"
                                    value={this.state.descricao}
                                    onChange={e => this.setState({ descricao: e.target.value })}
                                    className="form-control"
                                    id="inputDescricao"
                                    placeholder="Digite a descrição" />
                            </FormGroup>
                            <FormGroup htmlFor="inputCategoriaPai" label="Categoria pai:">
                                <SelectMenu lista={this.state.categorias}
                                        name='categoria'
                                        onChange={this.handleChange}
                                        clazz="form-control" />
                            </FormGroup>
                            <div className="d-flex justify-content-center mt-3" >
                                <Link to="/consulta-lancamentos" ><button onClick={this.cadastrar} type="button" className="btn btn-success" style={{ marginRight: "30px" }} >Salvar</button></Link>
                                <Link to="/consulta-lancamentos" ><button type="button" className="btn btn-danger">Cancelar</button></Link>
                            </div>
                        </div>
                    </div>
                </div>
            </Card>
        );
    }
}

export default CadastroCategoria;
